mapper.select:
SELECT * FROM orders o
JOIN services  s
ON o.`serviceID`=s.`serviceID`

WHERE o.userId = #{uid}
and o.orderStatus =#{statusId}
ORDER BY o.orderTime DESC

mapper.update:
UPDATE  orders
SET
    paymentTime=NOW(),
    orderStatus = 2
WHERE orderId= #{oid};

UPDATE USER
SET
    userMoney = userMoney-#{totalPrice},
    userIntegral = userIntegral+#{Integral}
WHERE userID = #{uid}

UPDATE orders
SET orderStatus = 6
WHERE orderID = #{oid}

mapper.insert:
INSERT INTO `hanting1`.`refund` (
`point`, `orderID`, `userID`, `refundReason`, `refundExplain`, `refundImg`, `applyRefundMoney`, `applicationTime` ) VALUES ( #{refund.point}, #{refund.orderID}, #{refund.userID}, #{refund.refundReason}, #{refund.refundExplain}, #{refund.refundImg}, #{refund.applyRefundMoney}, #{refund.applicationTime}
); UPDATE orders SET refundstatus = 1 WHERE orderID = #{refund.orderID}
